package practise;

public class Student {

	String name;
	int id;
	String dept;
	public Student() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Student(String name, int id, String dept) {
		super();
		this.name = name;
		this.id = id;
		this.dept = dept;
	}
	@Override
	public String toString() {
		return "Student [name=" + name + ", id=" + id + ", dept=" + dept + "]";
	}
	
	
	
	
	
	
	
	
	
	
	
	
	
}
